// =============================================
// CODE NODE 1 - C√ÅLCULO DE M√âTRICAS (sem descri√ß√£o, sem transcri√ß√£o)
// Sa√≠da: { estatisticas, videos[] }
// =============================================

const items = $input.all();

// Contexto (opcional) do "Edit Fields"
let contextoBusca = { nicho: "", resultadoMaximo: "", ordemClassificacao: "" };
try {
  const edit = $('Edit Fields').item.json;
  contextoBusca = {
    nicho: edit.Nicho || edit.nicho || "",
    resultadoMaximo: edit['Resultado maximo'] || edit.resultadoMaximo || "",
    ordemClassificacao: edit['Ordem de classifica√ß√£o'] || edit.ordemClassificacao || ""
  };
} catch { /* ok sem Edit Fields */ }

// ---------- Helpers ----------
const toInt = (x) => Number(String(x ?? 0).replace(/[^\d]/g, '')) || 0;

function daysSincePublished(dateString) {
  const d = new Date(dateString || Date.now());
  const diff = Math.abs(Date.now() - d.getTime());
  return Math.max(1, Math.ceil(diff / (1000 * 60 * 60 * 24)));
}

function durationToSeconds(hhmmss) {
  if (!hhmmss) return 0;
  const parts = String(hhmmss).split(':').map(n => parseInt(n, 10));
  if (parts.length === 3) return parts[0]*3600 + parts[1]*60 + parts[2];
  if (parts.length === 2) return parts[0]*60 + parts[1];
  return toInt(parts[0]);
}

// ---------- Processamento ----------
const processed = items.map((it, idx) => {
  const d = it.json || it;

  const title = (d.title || d['Titulo '] || d.Titulo || '').trim();
  const id = d.id || d.ID || `video_${idx}`;
  const url = d.url || d.Link || '';
  const thumb = d.thumbnailUrl || d.Thumb || '';
  const views = toInt(d.viewCount || d.View);
  const likes = toInt(d.likes || d.Likes);
  const comments = toInt(d.commentsCount || d['Comentarios '] || d.Comentarios);
  const subsCount = toInt(d.numberOfSubscribers || d.Inscritos || 1);
  const publishDate = d.date || d.Data || new Date().toISOString();
  const duration = d.duration || d['Dura√ß√£o'] || d.Duracao || '00:00:00';
  const channelName = (d.channelName || d.channelUsername || d.Canal || '').trim();
  const channelUrl = d.channelUrl || d.CanalLink || '';
  const channelId = d.channelId || d.IDCanal || '';

  // temporais
  const diasPublicado = daysSincePublished(publishDate);
  const duracaoSegundos = durationToSeconds(duration);

  // m√©tricas
  const engagementRate = views > 0 ? +((likes / views) * 100).toFixed(2) : 0;
  const likeRate = views > 0 ? +((likes / views) * 1000).toFixed(2) : 0;
  const commentRate = views > 0 ? +((comments / views) * 1000).toFixed(2) : 0;
  const totalEngagement = likes + comments;
  const totalEngagementRate = views > 0 ? +((totalEngagement / views) * 100).toFixed(2) : 0;
  const viewsPerDay = Math.round(views / Math.max(1, diasPublicado));
  const outlierScore = subsCount > 0 ? +((views / subsCount) * 100).toFixed(2) : 0;

  // formato
  let formatoVideo = 'm√©dio';
  if (duracaoSegundos < 180) formatoVideo = 'short';
  else if (duracaoSegundos < 600) formatoVideo = 'curto';
  else if (duracaoSegundos < 1200) formatoVideo = 'm√©dio';
  else formatoVideo = 'longo';

  return {
    // contexto
    nicho: contextoBusca.nicho,
    resultadoMaximo: contextoBusca.resultadoMaximo,
    ordemClassificacao: contextoBusca.ordemClassificacao,

    // identifica√ß√£o
    id,
    titulo: title,
    link: url,
    thumb,

    // dados brutos
    view: views,                 // <- usar sempre 'view' (singular)
    likes,
    comentarios: comments,
    duracao: duration,
    data: publishDate,

    // canal
    idCanal: channelId,
    canal: channelName,
    canalLink: channelUrl,
    inscritos: subsCount,

    // calculadas
    diasPublicado,
    duracaoSegundos,
    formatoVideo,
    engagementRate,
    likeRate,
    commentRate,
    totalEngagement,
    totalEngagementRate,
    viewsPerDay,
    outlierScore
  };
});

// ---------- Ordena√ß√£o & estat√≠sticas (usa 'view') ----------
processed.sort((a, b) => b.view - a.view);

const totalVideos = processed.length;
const totalViews = processed.reduce((s, v) => s + (v.view || 0), 0);
const totalLikes = processed.reduce((s, v) => s + (v.likes || 0), 0);
const avgViews = Math.round(totalViews / Math.max(1, totalVideos));
const avgEngagement = +(
  processed.reduce((s, v) => s + (v.engagementRate || 0), 0) / Math.max(1, totalVideos)
).toFixed(2);
const avgOutlier = +(
  processed.reduce((s, v) => s + (v.outlierScore || 0), 0) / Math.max(1, totalVideos)
).toFixed(2);
const avgViewsPerDay = Math.round(
  processed.reduce((s, v) => s + (v.viewsPerDay || 0), 0) / Math.max(1, totalVideos)
);

// ---------- Sa√≠da ----------
return [{
  json: {
    estatisticas: {
      totalVideos,
      totalViews,
      totalLikes,
      mediaViews: avgViews,
      mediaEngagement: avgEngagement,
      mediaOutlier: avgOutlier,
      mediaViewsPerDay: avgViewsPerDay,
      dataProcessamento: new Date().toISOString(),
      contextoBusca
    },
    videos: processed
  }
}];

// Mapeamento dos valores em portugu√™s para ingl√™s
const filterMap = {
  'Sem Filtro': null,
  'Relev√¢ncia': 'relevance',
  'Data do Upload': 'date',
  'Contagem de visualiza√ß√µes': 'views',
  'Rating': 'rating'
};

// Pega o valor do formul√°rio (vem como array)
const filtroArray = $input.item.json['Filtro por '];

// Pega o primeiro item do array (se existir)
const filtroSelecionado = filtroArray && filtroArray.length > 0 ? filtroArray[0] : null;

// Converte para o valor em ingl√™s
const filtroTraduzido = filtroSelecionado ? filterMap[filtroSelecionado] : null;

// Retorna o valor traduzido junto com os outros campos
return {
  json: {
    nicho: $input.item.json.Nicho,
    quantidade_esperada: $input.item.json['Quantidade esperada'],
    sorting_order: filtroTraduzido,
    submitted_at: $input.item.json.submittedAt,
    form_mode: $input.item.json.formMode
  }
};

// =============================================
// CODE NODE - BENCHMARKS DIN√ÇMICOS POR NICHO
// Otimizado para processar GRANDE VOLUME de dados
// Calcula benchmarks espec√≠ficos para cada nicho
// =============================================

const INICIO = Date.now();

// Pegar TODOS os itens da planilha
const todosVideos = $input.all();
const totalLinhas = todosVideos.length;

console.log(`üöÄ Iniciando processamento de ${totalLinhas} linhas...`);

// =============================================
// FUN√á√ïES ESTAT√çSTICAS OTIMIZADAS
// =============================================

function calcularEstatisticas(valores) {
  if (!valores || valores.length === 0) {
    return {
      media: 0, mediana: 0, desvioPadrao: 0, min: 0, max: 0,
      p10: 0, p25: 0, p50: 0, p75: 0, p90: 0, p95: 0, p99: 0
    };
  }

  // Ordenar uma √∫nica vez
  const sorted = valores.slice().sort((a, b) => a - b);
  const len = sorted.length;

  // M√©dia
  const soma = sorted.reduce((acc, val) => acc + val, 0);
  const media = soma / len;

  // Desvio padr√£o
  const variancia = sorted.reduce((acc, val) => acc + Math.pow(val - media, 2), 0) / len;
  const desvioPadrao = Math.sqrt(variancia);

  // Percentis
  const getPercentil = (p) => sorted[Math.floor(len * p)] || 0;

  return {
    media: parseFloat(media.toFixed(2)),
    mediana: getPercentil(0.50),
    desvioPadrao: parseFloat(desvioPadrao.toFixed(2)),
    min: sorted[0],
    max: sorted[len - 1],
    p10: getPercentil(0.10),
    p25: getPercentil(0.25),
    p50: getPercentil(0.50),
    p75: getPercentil(0.75),
    p90: getPercentil(0.90),
    p95: getPercentil(0.95),
    p99: getPercentil(0.99),
    totalAmostras: len
  };
}

// =============================================
// EXTRA√á√ÉO E NORMALIZA√á√ÉO DOS DADOS
// =============================================

const videosPorNicho = new Map();

todosVideos.forEach((item, index) => {
  const data = item.json;

  // Extrair nicho (suporta v√°rios formatos) - INCLUINDO "Termo de Pesquisa"
  const nicho = (
    data["Termo de Pesquisa"] ||
    data.termo_de_pesquisa ||
    data.nicho || 
    data.Nicho || 
    data.NICHO ||
    data.keyword ||
    data.Keyword ||
    data.query ||
