  };

  // Recalcular m√©tricas se necess√°rio (caso n√£o venham calculadas)
  if (video.engagementRate === 0 && video.view > 0) {
    video.engagementRate = parseFloat(((video.likes / video.view) * 100).toFixed(2));
  }
  if (video.viewsPerDay === 0 && video.diasPublicado > 0) {
    video.viewsPerDay = Math.round(video.view / video.diasPublicado);
  }
  if (video.outlierScore === 0 && video.inscritos > 0) {
    video.outlierScore = parseFloat(((video.view / video.inscritos) * 100).toFixed(2));
  }
  if (video.likeRate === 0 && video.view > 0) {
    video.likeRate = parseFloat(((video.likes / video.view) * 1000).toFixed(2));
  }
  if (video.commentRate === 0 && video.view > 0) {
    video.commentRate = parseFloat(((video.comentarios / video.view) * 1000).toFixed(2));
  }

  // Agrupar por nicho
  if (!videosPorNicho.has(nicho)) {
    videosPorNicho.set(nicho, []);
  }
  videosPorNicho.get(nicho).push(video);
});

console.log(`üìä Agrupado em ${videosPorNicho.size} nichos diferentes`);

// =============================================
// CALCULAR BENCHMARKS POR NICHO
// =============================================

const benchmarksPorNicho = {};

for (const [nicho, videos] of videosPorNicho) {
  console.log(`üìà Processando nicho: "${nicho}" (${videos.length} v√≠deos)`);

