    totalEngagementRate: videos.map(v => v.totalEngagementRate),
    viewsPerDay: videos.map(v => v.viewsPerDay),
    outlierScore: videos.map(v => v.outlierScore)
  };

  // Calcular estat√≠sticas para cada m√©trica
  const benchmarks = {};
  for (const [metrica, valores] of Object.entries(metricas)) {
    benchmarks[metrica] = calcularEstatisticas(valores);
  }

  // Salvar benchmarks do nicho (SEM niveisPerformance e insights)
  benchmarksPorNicho[nicho] = {
    nicho: nicho,
